function myStatus() {
    document.getElementById("statusDropdown").classList.toggle("show");
}

// Close the dropdown if the user clicks outside of it
window.onclick = function(event) {
  if (!event.target.matches('btn btn-fit-height dropdown-toggle btn dark btn-sm btn-outline sbold uppercase')) {

    var dropdowns = document.getElementsByClassName("dropdown-menu pull-center");
    var i;
    for (i = 0; i < dropdowns.length; i++) {
      var openDropdown = dropdowns[i];
      if (openDropdown.classList.contains('show')) {
        openDropdown.classList.remove('show');
      }
    }
  }
}

function myFunctionPending() {
    document.getElementById("status").innerHTML = "pending";
    document.getElementById("status").style = "background-color : #F7CA18 ";
}
function myFunctionApproved() {
    document.getElementById("status").innerHTML = "approved";
    document.getElementById("status").style = "background-color : #32C5D2 ";
}
function myFunctionDeclined() {
    document.getElementById("status").innerHTML = "declined";
    document.getElementById("status").style = "background-color : #E7505A ";
}